        [TestMethod]
        public void QuickUnionSimpleTest()
        {
            quickUnion.NumberToUnionFrom = new int[] { 3, 4, 8, 2, 5, 2, 5, 7, 4, 5, 0, 6 };
            quickUnion.NumberToUnionTo = new int[] { 4, 9, 0, 3, 6, 9, 9, 3, 8, 6, 2, 1 };

            quickUnion.Merge(quickUnion, MergeAlgorithms.QuickUnion);

            CollectionAssert.AreEqual(new int[10] { 1, 1, 9, 4, 9, 6, 9, 9, 0, 0 }, quickUnion.Output);
        }

